import styled from 'styled-components';
import '../../../index.css';
import theme from '../../Theme/theme';
import bp from '../../Theme/breakpoints';
import Footer from '../../Navigation/Footer/Footer';
import blueGlow from '../../../assets/images/BLUE_round.svg';
import SSTarrow from '../../../assets/images/uiButtons/double-arrow-up.svg';
import 'animate.css/animate.min.css';
import { AnimationOnScroll } from 'react-animation-on-scroll';
import ScrollToTop from 'react-scroll-to-top';
import { useEffect } from 'react';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // requires a loader
import { Carousel } from 'react-responsive-carousel';

const PageWrapper = styled.div`
	padding: 0 28px 64px 28px;
	max-width: 1560px;
	margin: 0 auto;
	height: 100%;
	background-color: transparent;
	position: relative;
	overflow: hidden;
	@media ${bp.sm} {
		position: relative;
	}
	@media ${bp.xl} {
		position: relative;
		max-height: 100%;
	}
`;

const AboutSection = styled.section`
	display: flex;
	justify-content: center;
	align-items: center;
	justify: center;
	border-radius: 50px;
	position: relative;
	flex-direction: column;
	background-color: transparent;
`;

const HeroSectionWrapper = styled.div`
	border-radius: 15px;
	padding: 48px 24px 0 24px;
	width: 100%;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	position: relative;
	background-color: transparent;
`;

const SectionWrapper = styled.div`
	border-radius: 15px;
	padding-top: 48px;
	padding-left: 24px;
	padding-right: 24px;
	width: 80%;
	display: flex;
	/* fix for Firefox */
	justify-content: center;
	align-items: center;
	position: relative;
	flex-direction: column;
	background-color: transparent;
	@media ${bp.xxs} {
		flex-direction: column;
	}
	@media ${bp.xs} {
		flex-direction: column;
	}
	@media ${bp.sm} {
		flex-direction: row;
	}
`;

const AboutSectionHeader = styled.h1`
	max-width: 700px;
	color: ${theme.color.text.primary};
	margin-bottom: 16px;
	text-align: flex-start;
	font-size: 36px;
	@media ${bp.sm} {
		text-align: left;
		font-size: 50px;
	}
`;

const AboutImageParent = styled.div`
	width: 100%;
	align-items: center;
	justify-content: center;
	display: flex;
	margin-bottom: 24px;
	@media ${bp.sm} {
		max-width: 400px;
		align-items: center;
		display: flex;
	}
`;

const AboutPara = styled.p`
	color: ${theme.color.text.primary};
	max-width: 700px;
	text-align: flex-start;
	font-size: 18px;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const RoadmapPara = styled.div`
	color: #fff;
	width: 50%;
	font-family: Arial;
	padding: 30px 30px 60px 30px;
	border: 4px solid ${props => roadmapColorPicker(props)};
	background: rgba(0, 0, 0, 0.5);
	backdrop-filter: blur(10px);
	border-radius: 20px;
	margin-bottom: 30px;
	margin-top: 30px;
	margin-right: 30px;
	margin-left: 30px;
	-webkit-box-shadow: 0px 0px 16px 0px ${props => roadmapColorPicker(props)};
	-moz-box-shadow: 0px 0px 16px 0px ${props => roadmapColorPicker(props)};
	box-shadow: 0px 0px 16px 0px ${props => roadmapColorPicker(props)};
	min-width: 300px;
	height: 12%;
	-webkit-flex-direction: column;
